//SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";

contract Lottery is VRFConsumerBaseV2 {
    address public owner;
    address payable[] public players;
    uint256 public lotteryid;
    mapping(uint256 => address payable) public lotteryhistory;

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords;
    }
    mapping(uint256 => RequestStatus) public s_requests; /* requestId --> requestStatus */
    VRFCoordinatorV2Interface COORDINATOR;

    // Your subscription ID.
    uint64 s_subscriptionId;

    // past requests Id.
    uint256[] public requestIds;
    uint256 public lastRequestId;

    bytes32 keyHash =
        0x79d3d8832d904592c0bf9818b621522c988bb8b0c05cdc3b15aea1b6e8db0c15;

    uint32 callbackGasLimit = 100000;

    uint16 requestConfirmations = 3;

    uint32 numWords = 2;

    constructor(uint64 subscriptionId)
        VRFConsumerBaseV2(0x2Ca8E0C643bDe4C2E08ab1fA0da3401AdAD7734D)
    {
        COORDINATOR = VRFCoordinatorV2Interface(
            0x2Ca8E0C643bDe4C2E08ab1fA0da3401AdAD7734D
        );
        s_subscriptionId = subscriptionId;
        owner = msg.sender;
        lotteryid = 1;
    }

    // Assumes the subscription is funded sufficiently.
    function requestRandomWords() public onlyOwner returns (uint256 requestId) {
        // Will revert if subscription is not set and funded.
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        return requestId;
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;
        pickWinner();
    }

    function getRequestStatus(uint256 _requestId)
        public
        view
        returns (bool fulfilled, uint256[] memory randomWords)
    {
        require(s_requests[_requestId].exists, "request not found");
        RequestStatus memory request = s_requests[_requestId];
        return (request.fulfilled, request.randomWords);
    }

    function getPotbalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getPlayers() public view returns (address payable[] memory) {
        return players;
    }

    function Play() public payable {
        require(msg.value >= 10000000000000000);
        //adding player address to an array
        players.push(payable(msg.sender));
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function pickWinner() public onlyOwner {
        requestRandomWords();
    }

    function Paywinner() public onlyOwner {
        uint256[] memory _randomWords;
        s_requests[lastRequestId].randomWords = _randomWords;
        uint256 lastRandomWords = _randomWords[_randomWords.length - 1];
        require(lastRandomWords > 0, "There must be a random number");

        uint256 index = lastRandomWords % players.length;
        players[index].transfer(address(this).balance);

        lotteryhistory[lotteryid] = players[index];
        lotteryid++;

        //reset contract state
        players = new address payable[](0);
    }

    function getWinner(uint256 _lotteryid)
        public
        view
        returns (address payable)
    {
        return lotteryhistory[_lotteryid];
    }

    function getlotteryid() public view returns (uint256) {
        return lotteryid;
    }
}
